* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

hr {
  width: 80vw;
}

body {
  background-color: #fff;
}

.background-img-section {
  background: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3)),
    url("../images/one-steak-on-grill-edit.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  height: 100vh;
  text-align: center;
}

/* mobile navbar */

#hamburger-link {
  display: none;
}

.navbar-open {
  width: 100vw;
  opacity: 1;
  height: 55vh;
  visibility: visible;
  background-color: #111;
}

.navbar-closed {
  opacity: 0;
  width: 0;
  height: 0;
  visibility: hidden;
  font-family: "Libre Baskerville", serif;
}

.mobile-navbar {
  display: none;
  flex-direction: column;
  background-color: #111;
  background-color: #121212;
  display: flex;
  flex-direction: column;
  text-align: left;
}

.mobile-navbar a {
  color: #fff;
  text-decoration: none;
  font-family: "Libre Baskerville", serif;
}

#hamburger-link span {
  font-size: 7rem;
  color: #fff;
}

#close-btn {
  position: absolute;
  right: 0;
}

.mobile-subnav {
  float: left;
  overflow: hidden;
}

.mobile-menu-links {
  display: flex;
  flex-direction: column;
  line-height: 2rem;
  align-items: flex-start;
  margin-left: 10vw;
  padding-top: 4vh;
}

.mobile-menu-links a {
  margin-right: 35vw;
}

.mobile-subnav-btn {
  background-color: transparent;
  color: #fff;
  border: none;
  font-size: 1.1rem;
  font-family: "Libre Baskerville", serif;
}

.mobile-subnav-content {
  display: none;
  text-align: left;
  padding-left: 5vw;
}

.mobile-subnav-content hr {
  width: 75vw;
}

.mobile-subnav:hover .mobile-subnav-content {
  display: block;
}

.mobile-subnav-content a {
  display: flex;
  flex-direction: column;
}
/* regular nav */
.navbar-container {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
}

.navbar {
  overflow: hidden;
  padding-top: 3vh;
  padding-left: 2vw;
}

.navbar a {
  color: white;
  text-decoration: none;
  font-family: "Libre Baskerville", serif;
  float: left;
  padding: 14px 16px;
}

.navbar a:hover {
  text-decoration: underline;
}

.subnav {
  float: left;
  overflow: hidden;
}

.subnav .subnav-btn {
  font-size: 1.3rem;
  border: none;
  outline: none;
  color: white;
  padding: 0.7rem 0.5rem 1.3rem 0.5rem;
  background-color: inherit;
  font-family: inherit;
  margin: 0;
}

.subnav-content {
  display: none;
  position: absolute;
  left: 0;
  background-color: #99752c;
  width: 100vw;
  z-index: 1;
}

.subnav-content a {
  float: left;
  color: white;
  text-decoration: none;
}

.subnav-content a:hover {
  background-color: #111;
  color: #99752c;
}

.subnav:hover .subnav-content {
  display: block;
}

.main-logo {
  color: white;
  text-decoration: none;
  font-size: 3rem;
  font-family: "Niconne", cursive;
  padding-top: 3vh;
  line-height: 3rem;
}

.facebook-btn {
  width: 6vw;
  height: 6vh;
}

.instagram-btn {
  width: 6vw;
  height: 6vh;
}

.social-media-btns {
  padding-top: 3vh;
  padding-right: 2vw;
}

.main-heading {
  color: #fff;
  margin-top: 12vh;
  font-family: "Montserrat", sans-serif;
  font-size: 3rem;
  text-align: center;
}

.sub-heading {
  font-size: 1.5rem;
  color: #fff;
  margin-top: 5vh;
  text-align: center;
}

.view-menu-btn {
  padding: 1rem;
  background-color: #751b05;
  color: #fff;
  font-family: "Libre Baskerville", serif;
  border: 2px solid #fff;
  border-radius: 50px;
  font-size: 1rem;
  margin-top: 5vh;
}

.view-menu-btn:hover {
  background-color: #99752c;
}

.view-menu-btn:hover {
  cursor: pointer;
}

.monthly-special-section {
  background-color: #fff;
  text-align: center;
  padding-bottom: 10vh;
}
.monthly-special-heading {
  text-align: center;
  margin-top: 8vh;
  color: #751b05;
  font-family: "Montserrat", sans-serif;
}

.monthly-special-msg {
  text-align: center;
  margin-top: 4vh;
  color: #333;
  font-family: "Libre Baskerville", serif;
}

.view-full-menu-btn {
  margin-top: 5vh;
  padding: 1rem;
  font-family: "Libre Baskerville", serif;
  border-radius: 50px;
  font-size: 1rem;
  background-color: transparent;
  color: #5c0e08;
  border: 2px solid #5c0e08;
}

.view-full-menu-btn:hover {
  background-color: #5c0e08;
  cursor: pointer;
  color: #fff;
}

.first-special {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  margin-top: 15vh;
  height: auto;
}

.special-names {
  color: #751b05;
  font-size: 2.3rem;
}

.special-description {
  margin-top: 5vh;
  font-size: 1.2rem;
}

.special-price {
  margin-top: 5vh;
  font-size: 1.5rem;
}

.special-images {
  border-radius: 50%;
  height: 40vh;
  width: 20vw;
}

.second-special {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  margin-top: 15vh;
  height: auto;
}

/* footer */
#footer {
  background-color: #111;
  color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 4vh;
  padding-bottom: 8vh;
  line-height: 3rem;
}

.main-footer-links {
  display: flex;
  justify-content: space-around;
  width: 25vw;
}

#footer-social-btns {
  padding-top: 3vh;
  padding-right: 2vw;
}

.main-footer-links a {
  font-size: 1rem;
  color: #fff;
  text-decoration: none;
  font-family: "Libre Baskerville", serif;
}

.footer-subnav a {
  color: #fff;
  text-decoration: none;
  font-family: "Libre Baskerville", serif;
}

.footer-subnav a:hover {
  color: #751b05;
}

.footer-subnav {
  display: flex;
  justify-content: space-around;
  width: 30vw;
}

.footer-phone-number:hover {
  color: #99752c;
}

.bottom-footer {
  background-color: #99752c;
  text-align: center;
  color: #fff;
  line-height: 5vh;
}
